sys_variable_value.value:
(function execute(inputs, outputs) {
  outputs.status = "Success";
  var responseBodyObj = {};
  if (inputs.responseBody && inputs.responseBody.indexOf('{') > -1)
    responseBodyObj = JSON.parse(inputs.responseBody);

  var errorHandler = new sn_sp_spoke.SPOErrorHandlerListItemMgmt(inputs.statusCode, responseBodyObj, inputs.responseBody, inputs);
  if (inputs.errorMessage) {
    outputs.status = "Error";
    var errorObj = errorHandler.getErrorMessage();
    outputs.errorMessage = errorObj.message;
    outputs.errorDetail = errorObj.detail;
  }

  if (outputs.status == "Success") {
    outputs.created = responseBodyObj.Created.replace('Z','');
    outputs.modified = responseBodyObj.Modified.replace('Z','');
    outputs.itemTitle = responseBodyObj.Title;
    outputs.itemstate = responseBodyObj.State;
    outputs.relativeUrl = responseBodyObj.ServerRelativeUrl;
  }

})(inputs, outputs);

