sys_variable_value.value:
(function execute(inputs, outputs) {
    //====== Load our Utility library
    var coreUtils = new sn_sp_spoke.SPOUtilsCore();

    //====== Make sure our mandatory inputs are filled. Runtime does not check if blank value is passed in from a data pill
    var mandatoryInputs = {
        tenant: "Tenant",
        listId: "List ID",
        listItemId: "List Item ID"
    };
    coreUtils.checkMandatoryInputs(inputs, mandatoryInputs);

    //===== handle our site location input parameter.
    outputs.siteLocation = coreUtils.handleURLPath(inputs.siteLocation);
})(inputs, outputs);

