sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...

  var accessGroup = inputs.access_group


  gs.info('TREVORRR' + accessGroup)
  //gs.info('TREVORRRRR' + inputs.access_group.getDisplayValue('u_choice_accessgroup'));

  outputs.result = accessGroup.indexOf(inputs.choice) > -1;

})(inputs, outputs);

