sys_variable_value.value:
(function execute(inputs, outputs) {



  var link_href = 
    "href=\"" + gs.getProperty('glide.servlet.uri')
  + inputs['portal'].url_suffix
  + "?id=ticket"
  + "&table="  + inputs['tablename']
  + "&sys_id=" + inputs['recordsysid'] 
  + "&view="   + inputs['view']
  + "\" target=\"_blank\">" 
  + inputs['text_to_display'];

  var record_link ="<a " + link_href + "</a>" ;

  var style = "style=\""
  + "color: black; background-color: #ffffff;"
  + "border-radius: 20px; border: 2px solid #27709b;"
  + "text-align: center;"
  + "text-decoration: none;"
  + "font-weight: bold; font-size: 16px; font-family: arial, helvetica, sans-serif;"
  + "line-height: 1.25em;"
  + "margin: 24px auto 6px; "
  + "padding: 10px 18px; "
  + "box-shadow: 5px 5px 5px #888; "
  + "width: 180px;" 
  + "\"";

  var record_link_styled = "<a " + style + link_href + "</a>" ;

  outputs['record_link'] = record_link;
  outputs['record_link_styled'] = record_link_styled;
})(inputs, outputs);

